    },
    methods: {
      ...mapActions('menu', ['fetchMenu', 'addMenuItem', 'updateMenuItem', 'deleteMenuItem']),
      openAddDialog() {
        this.dialogTitle = '添加菜品'
        this.formData = {
          item_id: null,
          name: '',
          description: '',
          price: '',
          image_url: '',
          is_available: true,
        }
        this.dialog = true
      },
      openEditDialog(item) {
        this.dialogTitle = '编辑菜品'
        this.formData = { ...item }
        this.dialog = true
      },
      closeDialog() {
        this.dialog = false
      },
      async submitForm() {
        if (this.$refs.form.validate()) {
          try {
            if (this.formData.item_id) {
              // 更新菜品
              await this.updateMenuItem(this.formData)
              this.snackbar.message = '菜品更新成功！'
              this.snackbar.color = 'success'
            } else {
              // 添加菜品
              await this.addMenuItem(this.formData)
              this.snackbar.message = '菜品添加成功！'
              this.snackbar.color = 'success'
            }
            this.snackbar.show = true
            this.dialog = false
          } catch (error) {
            this.snackbar.message = '操作失败，请重试。'
            this.snackbar.color = 'error'
            this.snackbar.show = true
            console.error('提交表单失败：', error)
          }
        }
      },
      deleteItem(itemId) {
        this.itemToDelete = this.menuItems.find(item => item.item_id === itemId)
        this.deleteDialog = true
      },
      closeDeleteDialog() {
        this.deleteDialog = false
        this.itemToDelete = null
      },
      async confirmDelete() {
        try {
          await this.deleteMenuItem(this.itemToDelete.item_id)
          this.snackbar.message = '菜品删除成功！'
          this.snackbar.color = 'success'
          this.snackbar.show = true
        } catch (error) {
          this.snackbar.message = '删除失败，请重试。'
          this.snackbar.color = 'error'
          this.snackbar.show = true
          console.error('删除菜品失败：', error)
        } finally {
          this.deleteDialog = false
          this.itemToDelete = null
        }
      },
    },
    created() {
      this.fetchMenu()
    },
  }
  </script>

