<template>
  <v-container>
    <h1>下订单</h1>
    <v-row>
      <v-col
        v-for="item in menuItems"
        :key="item.item_id"
        cols="12"
        sm="6"
        md="4"
      >
        <v-card>
          <v-img :src="item.image_url || defaultImage" height="200px"></v-img>
          <v-card-title>{{ item.name }}</v-card-title>
          <v-card-subtitle>价格: ¥{{ item.price }}</v-card-subtitle>
          <v-card-text>
            <p>{{ item.description }}</p>
            <v-text-field
              v-model.number="item.quantity"
              label="数量"
              type="number"
              min="1"
              @input="validateQuantity(item)"
            ></v-text-field>
          </v-card-text>
          <v-card-actions>
            <v-btn color="primary" @click="addToCart(item)">添加到购物车</v-btn>
          </v-card-actions>
        </v-card>
      </v-col>
    </v-row>

    <!-- 购物车 -->
    <v-card class="mt-5">
      <v-card-title>购物车</v-card-title>
      <v-card-text>
        <v-list>
          <v-list-item
            v-for="cartItem in cart"
            :key="cartItem.item_id"
          >
            <v-list-item-content>
              <v-list-item-title>{{ cartItem.name }}</v-list-item-title>
              <v-list-item-subtitle>数量: {{ cartItem.quantity }}</v-list-item-subtitle>
            </v-list-item-content>
            <v-list-item-action>
              <v-btn icon @click="removeFromCart(cartItem)">
                <v-icon color="red">mdi-delete</v-icon>
              </v-btn>
            </v-list-item-action>
          </v-list-item>
        </v-list>
        <v-divider></v-divider>
        <v-row justify="end">
          <v-col cols="12" class="text-right">
            <h3>总价: ¥{{ totalPrice.toFixed(2) }}</h3>
            <v-form ref="orderForm" v-model="valid" class="mt-3">
              <!-- 配送地址 -->
              <v-text-field
                v-model="deliveryAddress"
                label="配送地址"
                :rules="[v => !!v || '配送地址为必填项']"
                required
              ></v-text-field>

              <!-- 配送时间 -->
              <v-text-field
                v-model="deliveryTime"
                label="配送时间"
                type="time"
                :rules="[v => !!v || '配送时间为必填项']"
                required
              ></v-text-field>
            </v-form>
            <v-btn color="success" @click="submitOrder" :disabled="!valid || cart.length === 0">
              提交订单
            </v-btn>
          </v-col>
        </v-row>
      </v-card-text>
    </v-card>

    <!-- 成功与错误消息 -->
    <v-snackbar v-model="snackbar.show" :color="snackbar.color" timeout="6000">
      {{ snackbar.message }}
      <v-btn color="white" text @click="snackbar.show = false">关闭</v-btn>
    </v-snackbar>
  </v-container>
</template>

<script>
import { mapGetters, mapActions } from 'vuex'

export default {
  name: 'OrdersPage',
  data() {
    return {
      cart: [],
      defaultImage: 'https://via.placeholder.com/200',
      deliveryAddress: '',
      deliveryTime: '',
      valid: false,
      snackbar: {
        show: false,
        message: '',
        color: '',
      },
    }
  },
  computed: {
    ...mapGetters('menu', ['getMenuItems']),
    ...mapGetters('auth', ['getUser']),
    menuItems() {
      return this.getMenuItems
    },
    totalPrice() {
      return this.cart.reduce((total, item) => total + parseFloat(item.price) * item.quantity, 0)
    },
  },
  methods: {
    ...mapActions('menu', ['fetchMenu']),
    ...mapActions('orders', ['createOrder']),

    validateQuantity(item) {
      if (item.quantity < 1) {
        item.quantity = 1
      }
    },

    addToCart(item) {
      const existingItem = this.cart.find(cartItem => cartItem.item_id === item.item_id)
      if (existingItem) {
        existingItem.quantity += item.quantity
      } else {
        this.cart.push({ ...item, quantity: item.quantity || 1 })
      }
      this.snackbar.message = '已添加到购物车！'
      this.snackbar.color = 'success'
      this.snackbar.show = true
    },

    removeFromCart(item) {
      this.cart = this.cart.filter(cartItem => cartItem.item_id !== item.item_id)
      this.snackbar.message = '已从购物车移除！'
      this.snackbar.color = 'warning'
      this.snackbar.show = true
    },

    async submitOrder() {
      if (!this.$refs.orderForm.validate()) {
        return
      }

      try {
        const user = this.getUser
        if (!user) {
          throw new Error('用户未登录')
        }

        // 确保配送时间已选择
        if (!this.deliveryTime) {
          this.snackbar.message = '请选择配送时间。'
          this.snackbar.color = 'error'
          this.snackbar.show = true
          return
        }

        // 获取当前日期
        const today = new Date()
        const year = today.getFullYear()
        const month = String(today.getMonth() + 1).padStart(2, '0')
        const day = String(today.getDate()).padStart(2, '0')

        // 格式化时间，确保有秒数
        const [hours, minutes] = this.deliveryTime.split(':')
        const combinedDeliveryTime = `${year}-${month}-${day} ${hours}:${minutes}:00`

        const orderData = {
          customer_id: user.user_id, // 确保包含customer_id
          delivery_time: combinedDeliveryTime,
          delivery_address: this.deliveryAddress,
          items: this.cart.map(item => ({
            item_id: item.item_id,
            quantity: item.quantity,
          })),
        }
        await this.createOrder(orderData)
        this.snackbar.message = '订单已提交成功！'
        this.snackbar.color = 'success'
        this.snackbar.show = true
        this.cart = []
        this.deliveryAddress = ''
        this.deliveryTime = ''
      } catch (error) {
        this.snackbar.message = '订单提交失败，请重试。'
        this.snackbar.color = 'error'
        this.snackbar.show = true
        console.error('订单提交失败：', error)
      }
    },
  },
  created() {
    this.fetchMenu()
  },
}
</script>

<style scoped>
.mt-5 {
  margin-top: 2rem;
}
.mt-3 {
  margin-top: 1rem;
}
</style>
