            :rules="[v => !!v || '密码为必填项']"
            required
          ></v-text-field>

          <v-btn :disabled="!valid || loading" color="primary" type="submit">
            登录
          </v-btn>
        </v-form>
      </v-card-text>
      <v-card-actions>
        <v-spacer></v-spacer>
        <v-btn text @click="$router.push('/register')">还没有账户？注册</v-btn>
      </v-card-actions>
    </v-card>

    <!-- 显示消息 -->
    <v-snackbar v-model="snackbar" :color="snackbarColor" timeout="6000">
      {{ snackbarMessage }}
      <v-btn color="white" text @click="snackbar = false">关闭</v-btn>
    </v-snackbar>
  </v-container>
</template>

<script>
import { mapActions } from 'vuex'

export default {
  name: 'UserLogin',
  data() {
    return {
      valid: false,
      username: '',
      password: '',
      loading: false,
      snackbar: false,
      snackbarMessage: '',
      snackbarColor: '',
    }
  },
  methods: {
    ...mapActions('auth', ['login']),
    async submit() {
      if (this.$refs.form.validate()) {
        this.loading = true
        try {
          const credentials = {
            username: this.username,
            password: this.password,
          }
          await this.login(credentials)
          this.snackbarMessage = '登录成功！'
          this.snackbarColor = 'success'
          this.snackbar = true
          console.log('登录成功，跳转首页')
          setTimeout(() => {
            this.$router.push('/')
          }, 1500) // 1.5 秒后跳转
        } catch (error) {
          this.snackbarMessage = error.response?.data?.message || '登录失败，请检查用户名和密码。'
          this.snackbarColor = 'error'
          this.snackbar = true
          console.error('登录失败：', error)
        } finally {
          this.loading = false
        }
      }
    },
  },
}
