// src/store/modules/menu.js

import apiClient from '@/services/api'

const state = {
  menuItems: [],
}

const mutations = {
  SET_MENU(state, menu) {
    state.menuItems = menu
  },
  ADD_MENU_ITEM(state, item) {
    state.menuItems.push(item)
  },
  UPDATE_MENU_ITEM(state, updatedItem) {
    const index = state.menuItems.findIndex(item => item.item_id === updatedItem.item_id)
    if (index !== -1) {
      state.menuItems.splice(index, 1, updatedItem)
    }
  },
  DELETE_MENU_ITEM(state, itemId) {
    state.menuItems = state.menuItems.filter(item => item.item_id !== itemId)
  },
}

const actions = {
  // 获取菜单
  async fetchMenu({ commit }) {
    try {
      const response = await apiClient.get('/menu')
      commit('SET_MENU', response.data)
    } catch (error) {
      console.error('获取菜单失败：', error)
      throw error
    }
  },

  // 添加菜品
  async addMenuItem({ commit }, newItem) {
    try {
      const response = await apiClient.post('/menu', newItem)
      commit('ADD_MENU_ITEM', { ...newItem, item_id: response.data.item_id })
    } catch (error) {
      console.error('添加菜品失败：', error)
      throw error
    }
  },

  // 更新菜品
  async updateMenuItem({ commit }, updatedItem) {
    try {
      await apiClient.put(`/menu/${updatedItem.item_id}`, updatedItem)
      commit('UPDATE_MENU_ITEM', updatedItem)
    } catch (error) {
      console.error('更新菜品失败：', error)
      throw error
    }
  },

  // 删除菜品
  async deleteMenuItem({ commit }, itemId) {
    try {
      await apiClient.delete(`/menu/${itemId}`)
      commit('DELETE_MENU_ITEM', itemId)
    } catch (error) {
      console.error('删除菜品失败：', error)
      throw error
    }
  },
}

const getters = {
  getMenuItems: (state) => state.menuItems,
}

export default {
  namespaced: true,
  state,
  mutations,
  actions,
  getters,
}

